import React, { useState } from 'react';

const KeyboardPage = () => {
  // List of alphabets and numbers
  const keys = [
    ...'AB',
  ];

  // State to store the selected key
  const [selectedKey, setSelectedKey] = useState(null);

  // Function to handle button clicks
  const handleClick = (key) => {
    setSelectedKey(key);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Click any key to display my drawings</h1>
      
      {/* Display buttons for each key */}
      <div className="grid grid-cols-6 gap-4 mb-8">
        {keys.map((key, index) => (
          <button
            key={index}
            onClick={() => handleClick(key)}
            className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-300"
          >
            {key}
          </button>
        ))}
      </div>

      {/* Display image based on selected key */}
      {selectedKey && (
        <div className="mt-8 text-center">
          <h2 className="text-xl font-semibold mb-4">You clicked: {selectedKey}</h2>
          <img
            src={`images/${selectedKey}.jpeg`} // path to your local image
            className="border rounded-lg"
          />
        </div>
      )}
    </div>
  );
};

export default KeyboardPage;
